svg.script:
const lines = [
  "Loading skills...",
  "C#",
  "C++",
  "Python",
  "JavaScript",
  "HTML / CSS / Lua",
  "Other tools: Git, Docker, etc."
];

const tspans = [
  document.getElementById("line1"),
  document.getElementById("line2"),
  document.getElementById("line3"),
  document.getElementById("line4"),
  document.getElementById("line5"),
  document.getElementById("line6"),
  document.getElementById("line6") // reuse last line for extra text
];

let delay = 0;

lines.forEach((line, i) => {
  line.split("").forEach((char, j) => {
    setTimeout(() => {
      tspans[i].textContent += char;
    }, delay);
    delay += 50; // 50ms per character
  });
  delay += 200; // small pause between lines
});
